    <script>
        var userVote = null;
        async function postComment(mangaId, chapterId, content, parentCommentId) {
            const response = await $.ajax({
                type: 'POST',
                xhrFields: { withCredentials: true },
                url:
                    '/comments' +
                    (!!parentCommentId ? `/${parentCommentId}/reply` : ''),
                contentType: 'application/json',
                data: JSON.stringify({ mangaId, chapterId, content, _token: '{{ csrf_token() }}' }),
                dataType: 'json',
            }).catch(() => {
                return null;
            });

            if (response && response.status === 200) {
                return response.data;
            }

            return null;
        }

        async function getComment(mangaId, chapterId, page, pageSize, orderBy) {
            const url = new URL('{{route('getComments')}}');

            url.searchParams.append('mangaId', mangaId);
            if (chapterId) {
                url.searchParams.append('chapterId', chapterId);
            }

            if (page) {
                url.searchParams.append('page', page);
            }

            if (pageSize) {
                url.searchParams.append('pageSize', pageSize);
            }

            if (orderBy) {
                url.searchParams.append('orderBy[]', orderBy);
            }

            const response = await $.ajax({
                type: 'GET',
                xhrFields: { withCredentials: true },
                url: url.toString(),
                contentType: 'application/json',
            }).catch(() => {
                return null;
            });

            if (response && response.status === 200) {
                return response.data;
            }

            return null;
        }

        async function likeComment(commentId) {
            const response = await $.ajax({
                type: 'PATCH',
                xhrFields: { withCredentials: true },
                url: `/comments/${commentId}/like`,
                contentType: 'application/json',
                data: JSON.stringify({ _token: '{{ csrf_token() }}' }),
                dataType: 'json',
            }).catch(() => {
                return null;
            });

            if (response && response.status === 200) {
                return response.data;
            }

            return null;
        }

        async function voteManga(mangaId, vote) {
            const response = await $.ajax({
                type: 'POST',
                url: '/mangas/' + mangaId + '/vote',
                contentType: 'application/json',
                data: JSON.stringify({ vote, _token: '{{ csrf_token() }}' }),
                dataType: 'json',
            }).catch(() => {
                return null;
            });

            if (response && response.status === 200) {
                return response.data;
            }

            return null;
        }

        async function followManga(mangaId) {
            const response = await $.ajax({
                type: 'POST',
                url: '/mangas/' + mangaId + '/follow',
                contentType: 'application/json',
                data: JSON.stringify({ _token: '{{ csrf_token() }}' }),
            }).catch(() => {
                return null;
            });

            if (response && response.status === 200) {
                return response.data;
            }

            return null;
        }

    </script>
